import { assert, expect } from 'chai';
import Allproducts, { getProducts, getProduct, addStars, increaseQuantity, decreaseQuantity, addToCart } from './Allproducts';
import {totalize} from './solutioncode';

require('isomorphic-fetch'); // enables the use of javascript fetch api
//const request = require("supertest")("https://jsonplaceholder.typicode.com");
const request = require("supertest")("http://localhost:8090/api/products");

// writing tests: arrange - what we need to do test, act - do, assert - results match as expect

// test to display all products from an api
// “done” parameter, when present in your callback function, tells Mocha that you are writing an asynchronous test.
// waiting for the async function to finish – which is facilitated by either calling the `done()` function
describe("GET /products", function () {
  it("returns data", function (done) {
    this.timeout(500);

    function theProducts() {
      fetch(request)
        .then(function (response) {
          responseMethod(response);
        })
        .then(function (data) {
          return (data);
        });
    }

    const expected = theProducts();
    const actual = getProducts();
    expect(actual).to.deep.equal(expected);
    done();
    });
});

// passing test which displays one product when name is passed
describe('getProduct - basic functionality', () => {
  it('returns one object when passed', () => {
    let chocolate = new Allproducts("Chocolate", "/images/chocolate.jpg", 2.00, 3, "sweets");
    const expected = { name: "Chocolate", image: "/images/chocolate.jpg", price: 2.00, likes: 3, category: "sweets" };
    const actual = getProduct(chocolate);
    expect(actual).to.deep.equal(expected);
  });
});

// cart


describe('basket - total', () => {

  it('is zero when basket is empty', () => {
    function addItems() {
  const basket = [];
  assert.equal(totalize(basket), 0.0);
    }
});

it('is unit price of single item in basket', () => {
  function addItems() {
    const basket = [{
      unitpPrice: 10.0, quantity:1
    }];

  //  const basket1 = new Allproducts("Chocolate", "/images/chocolate.jpg", 2.00, 3, "sweets");

    assert.equal(totalize(basket), 10.0);
  }
});

it('is unit price * quantity of single item in basket', () => {
  function addItems() {
    const basket = [{
      unitpPrice: 10.0, quantity:2
    }];

  //  const basket1 = new Allproducts("Chocolate", "/images/chocolate.jpg", 2.00, 3, "sweets");

    assert.equal(totalize(basket), 20.0);
  }
});

it('is sum of all items in basket', () => {
  function addItems() {
    const basket = [
      {unitpPrice: 10.0, quantity:1  },
      {unitpPrice: 20.0, quantity:2  },
      {unitpPrice: 30.0, quantity:1  }
  ];

  //  const basket1 = new Allproducts("Chocolate", "/images/chocolate.jpg", 2.00, 3, "sweets");

    assert.equal(totalize(basket), 80.0);
  }
});

});

